                   <img src={imgem} width={150} height={150}/> 
                </div>   

                <div className='nomeProduto'>
                    {nomeProduto}
                </div>
                <div className='dataProduto'>
                    {valorProduto}
                </div>
                <div className='codProduto'>
                     {codProduto}
                </div>
            </div>


    )
}
